mapper.insert:
insert into account (
	  username
	, password
	, groupname
	, name
	, email
	, whiteip
	, fee
	, account_non_expired
	, account_non_locked
	, credentials_non_expired
	, enabled
) values (
	  #{username}
	, #{password}
	, #{groupname}
	, #{name}
	, #{email}
	, #{whiteip}
	, #{fee}
	, #{accountNonExpired}
	, #{accountNonLocked}
	, #{credentialsNonExpired}
	, #{enabled}
)

insert into account_roles (
	  username
	, role
) values (
	  #{username}
	, #{role} 
)
